import React, { useState } from "react";

import Button from "../../UI/Button/AddButton";
import Modal from "../../UI/Modal/FormModal";

import { Row } from "reactstrap";

import classes from "./Desk.module.css";
import Notes from "../../components/Note/Note";

const Desk = () => {
  const [notes, setNotes] = useState([{ text: "Get this project Done" }]);
  const [showModal, setShowModal] = useState(false);

  const addNoteHandler = () => {
    console.log("Note added to array");
    let newNote = { text: "You have no excuse" };
    setNotes(notes.concat(newNote));
    console.log(notes[0].text);
  };

  const toggleModalHandler = () => {
    setShowModal(!showModal);
    console.log(showModal);
  };

  const formDataHandler = (e) => {
    setNotes(e.target.value);
    console.log(notes);
  };

  const stickyNotes = notes.map((note) => <Notes text={note.text} />);
  const modalForm = (
    <Modal
      openModal={showModal}
      toggleModal={toggleModalHandler}
      note={notes}
      changeText={formDataHandler}
    />
  );

  return (
    <div className={classes.Desk}>
      Desk background
      <button onClick={addNoteHandler}>test</button>
      <Row>{stickyNotes}</Row>
      {modalForm}
      <Button addNote={toggleModalHandler} />
    </div>
  );
};

export default Desk;
