import React, { useState } from "react";

import { Row } from "reactstrap";

import classes from "./Desk.module.css";
import Notes from "../../components/Note/Note";

function Desk() {
  const [note, setNote] = useState({
    notes: [{ note: "test" }],
  });

  const addNoteHandler = (prevState) => {
    console.log("Note added to array");
    setNote({ ...prevState, note: "another test" });
  };

  return (
    <div className={classes.Desk}>
      Desk background
      <button onClick={addNoteHandler}>test</button>
      <Row>
        <Notes />
        <Notes />
        <Notes />
      </Row>
    </div>
  );
}

export default Desk;
