import React, { useState } from "react";

import Button from "../../UI/Button/AddButton";
import Modal from "../../UI/Modal/FormModal";

import { Row } from "reactstrap";

import classes from "./Desk.module.css";
import Notes from "../../components/Note/Note";

const Desk = () => {
  const [note, setNote] = useState([{ text: "Get this project Done" }]);
  const [showModal, setShowModal] = useState(true);

  const addNoteHandler = () => {
    console.log("Note added to array");
    let newNote = { text: "You have no excuse" };
    setNote(note.concat(newNote));
    console.log(note[0].text);
  };

  const toggleModalHandler = () => {
    setShowModal = false;
  };

  const notes = note.map((note) => <Notes text={note.text} />);
  const modalForm = <Modal openModal={showModal} /> ? showModal : null;

  return (
    <div className={classes.Desk}>
      Desk background
      <button onClick={addNoteHandler}>test</button>
      <Row>{notes}</Row>
      <div>{modalForm}</div>
      <Modal openModal={showModal} />
      <button onClick={toggleModalHandler}>close modal</button>
      <Button />
    </div>
  );
};

export default Desk;
